export type AllModel = (typeof ALL_MODELS)[number];

// Legacy type for backward compatibility
export type OpenRouterModel = AllModel;
export const OPENROUTER_MODELS = ALL_MODELS;

// CLI Configuration
export interface ClaudishConfig {
  model?: OpenRouterModel | string; // Optional - will prompt if not provided
  port?: number;
  autoApprove: boolean;
  dangerous: boolean;
  interactive: boolean;
  debug: boolean;
  logLevel: "debug" | "info" | "minimal"; // Log verbosity level (default: info)
  quiet: boolean; // Suppress [claudish] log messages (default true in single-shot mode)
  jsonOutput: boolean; // Output in JSON format for tool integration
  monitor: boolean; // Monitor mode - proxy to real Anthropic API and log everything
  stdin: boolean; // Read prompt from stdin instead of args
  openrouterApiKey?: string; // Optional in monitor mode
  poeApiKey?: string; // Poe API key for poe/ models
  poeWarmup?: boolean; // Pre-warm Poe connection on startup
  anthropicApiKey?: string; // Required in monitor mode
  agent?: string; // Agent to use for execution (e.g., "frontend:developer")
  freeOnly?: boolean; // Show only free models in selector
  profile?: string; // Profile name to use for model mapping
  provider?: string; // Provider filter for model listings (openrouter, poe, all)
  claudeArgs: string[];

  // Model Mapping
  modelOpus?: string;
  modelSonnet?: string;
  modelHaiku?: string;
  modelSubagent?: string;

  // Cost tracking
  costTracking?: boolean;
  auditCosts?: boolean;
  resetCosts?: boolean;
}

// Anthropic API Types
export interface AnthropicMessage {
  role: "user" | "assistant";
  content: string | ContentBlock[];
}

export interface ContentBlock {
  type: "text" | "image";
  text?: string;
  source?: {
    type: "base64";
    media_type: string;
    data: string;
  };
}

export interface AnthropicRequest {
  model: string;
  messages: AnthropicMessage[];
  max_tokens?: number;
  temperature?: number;
  top_p?: number;
  stream?: boolean;
  system?: string;
}

export interface AnthropicResponse {
  id: string;
  type: "message";
  role: "assistant";
  content: ContentBlock[];
  model: string;
  stop_reason: string | null;
  usage: {
    input_tokens: number;
    output_tokens: number;
  };
}

// OpenRouter API Types
export interface OpenRouterMessage {
  role: "system" | "user" | "assistant";
  content: string;
}

export interface OpenRouterRequest {
  model: string;
  messages: OpenRouterMessage[];
  max_tokens?: number;
  temperature?: number;
  top_p?: number;
  stream?: boolean;
}

export interface OpenRouterResponse {
  id: string;
  model: string;
  choices: Array<{
    message: {
      role: "assistant";
      content: string;
    };
    finish_reason: string | null;
  }>;
  usage: {
    prompt_tokens: number;
    completion_tokens: number;
    total_tokens: number;
  };
}

// Proxy Server
export interface ProxyServer {
  port: number;
  url: string;
  shutdown: () => Promise<void>;
}

// Multi-Provider Model System
export interface UnifiedModel {
  id: string;
  name: string;
  description: string;
  provider: 'openrouter' | 'poe' | 'native';
  context_length: number;
  pricing: {
    prompt: string;
    completion: string;
  };
  priority?: number;
}

// Provider interface for extensibility
export interface ModelProvider {
  name: string;
  fetchModels(): Promise<UnifiedModel[]>;
  transformModel?(model: UnifiedModel): UnifiedModel;
}
